sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;
	$scope.filter = false;
	$scope.showValues = false;
	c.showRecords = function()
	{
		$scope.filter = true;
	$scope.showValues = true;
		if(c.name == "desc")
			c.orderName = "-" + "name";
		else
			c.orderName = "name";
		$scope.data.display = true;
		c.server.update();
	}
};

sp_widget.css:
td,th
{
  padding : 10px;
  border-radius : 5px;
}

th
{
  text-align : center;
}

input
{
  margin : 5px;
}

button
{
  margin-bottom : 15px;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	if(input)
		{

			var cnt = 10;
			data.table = {
				list : []
			}
			var gr = new GlideRecord("core_company");
			if(input.name)
				gr.addQuery('name', input.name);
			else
				{
			if(input.city)
				gr.addQuery('city', input.city);
			if(input.state)
			gr.addQuery('state', input.state);

			if (!input.city && !input.state)
				gr.addEncodedQuery('nameISNOTEMPTY^phoneISNOTEMPTY^cityISNOTEMPTY^stateISNOTEMPTY');
				}
			gr.query();
			while(gr.next() && cnt--)
				{
					var obj = {};
					obj.name = gr.getValue('name');
					obj.phone = gr.getValue('phone');
					obj.address = gr.getValue('city') + ", " + gr.getValue('state');
					data.table.list.push(obj);

				}
		}
})();
